    <div class="min-w-0 flex-1">
        <div class="px-6 flex w-full items-center justify-between md:w-auto">
            <a routerLink="/feeds">
                <span class="sr-only">Writer's Meet</span>
                <img routerLink="/feeds" class="mx-auto h-20 w-30 "
                    src="./assets/img/Lovepik_com-400262015-green-plants-hand-painted-leaves.png"
                    alt="Writer's Meet Logo">
            </a>
        </div>
      <div class="mt-1 flex flex-col sm:mt-0 sm:flex-row sm:flex-wrap sm:space-x-6">
