import { Component, OnDestroy, OnInit } from '@angular/core';
import { ApiService } from 'src/app/services/api.service';
import { Subscription } from 'rxjs';
import { ActivatedRoute } from '@angular/router';
import { BlogPost } from 'src/app/models/blogPost';
import { Router } from '@angular/router';
import { HttpEventType } from '@angular/common/http';
import { NewBlogPost } from 'src/app/models/newBlogPost';

@Component({
  selector: 'app-new-article',
  templateUrl: './new-article.component.html',
  styleUrls: ['./new-article.component.css']
})
export class NewArticleComponent implements OnDestroy{

  subscription?: Subscription;
 _article!:NewBlogPost;

_article.co:string="";

  constructor(
    private apiService: ApiService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

postArticle(){
  this.subscription=this.apiService.postArticle(this._article).subscribe(
    response=>{
        console.log(response);
    }
  );
}


url: any; //Angular 11, for stricter type
msg = "";

//selectFile(event) { //Angular 8
selectFile(event: any) { //Angular 11, for stricter type
  if(!event.target.files[0] || event.target.files[0].length == 0) {
    this.msg = 'You must select an image';
    return;
  }

  var mimeType = event.target.files[0].type;

  if (mimeType.match(/image\/*/) == null) {
    this.msg = "Only images are supported";
    return;
  }

  var reader = new FileReader();
  reader.readAsDataURL(event.target.files[0]);

  reader.onload = (_event) => {
    this.msg = "";
    this.url = reader.result; 
    this._article.coverImagePath=this.url;
    console.log(this.url);
  }
}

  ngOnDestroy(): void {
    if (this.subscription) {
      this.subscription.unsubscribe();
    }
  }
}
