//     const [file] = event.target.files;
//     reader.readAsDataURL(file);
//     reader.onload = () => {
//       this._article.coverImagePath = reader.result as string;
//     };
//   }
// }

  ngOnDestroy(): void {
    if (this.subscription) {
      this.subscription.unsubscribe();
    }
  }
}
