@Component({
  selector: 'app-new-article',
  templateUrl: './new-article.component.html',
  styleUrls: ['./new-article.component.css']
})
export class NewArticleComponent implements OnDestroy{

  subscription?: Subscription;
 _article!:BlogPost;

