import { Component, OnDestroy, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Subscription } from 'rxjs/internal/Subscription';
import { Verification } from 'src/app/models/verification';
import { ApiService } from 'src/app/services/api.service';

@Component({
  selector: 'app-verification',
  templateUrl: './verification.component.html',
  styleUrls: ['./verification.component.css']
})
export class VerificationComponent implements OnInit, OnDestroy {

_email=this.route.snapshot.paramMap.get('emailAddress')!;
_token=this.route.snapshot.paramMap.get('token')!;
  subscription?: Subscription;
  message?: string;

  constructor(
    private apiService: ApiService,
    private route: ActivatedRoute,

  ) {}

  ngOnInit(): void {
    this.verify = {} as Verification;
  }

  verifyUser():any{
    this.subscription = this.apiService.verifyAuthor(this._email,).subscribe( response => {this.message=response.message
      console.log(this.message);
    });
    }

    ngOnDestroy(): void {
      if (this.subscription) {
        this.subscription.unsubscribe();
      }
    }
}
